CREATE DATABASE my_db;
USE my_db;

CREATE TABLE stockinfo (
    exchangetype char(1) NOT NULL,
    stockcode char(6) NOT NULL,
    lastprice double(10, 2) NOT NULL
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4; 

CREATE TABLE holdinginfo (
    clientid char(12) NOT NULL,
    exchangetype char(1) NOT NULL,
    stockcode char(6) NOT NULL,
    lastprice double(10, 2) NOT NULL,
    holdamount int(11) NOT NULL,
    marketvalue double(10, 2) NOT NULL
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4; 






